mapper.select:
select

from ps_user
where login_name = #{loginName, jdbcType=VARCHAR}

select

from ps_user


        nick_name LIKE #{nickName,jdbcType=VARCHAR}


        AND sex = #{sex,jdbcType=CHAR}


        AND id_no LIKE #{idNo,jdbcType=VARCHAR}


        AND u_state = #{uState,jdbcType=VARCHAR}


        AND login_time >= #{startTime}


        AND login_time <= #{endTime}


order by login_time desc
limit #{start,jdbcType=TINYINT}, #{count,jdbcType=TINYINT}

select count(id)
from ps_user


        nick_name LIKE #{nickName,jdbcType=VARCHAR}


        AND sex = #{sex,jdbcType=CHAR}


        AND id_no LIKE #{idNo,jdbcType=VARCHAR}


        AND u_state = #{uState,jdbcType=VARCHAR}


        AND login_time >= #{startTime}


        AND login_time <= #{endTime}
