mapper.insert:
insert into sign_in (id, course_id, signer_id,
sign_in_time, score_time, score
)
values (#{id,jdbcType=BIGINT}, #{courseId,jdbcType=BIGINT}, #{signerId,jdbcType=BIGINT},
#{signInTime,jdbcType=TIMESTAMP}, #{scoreTime,jdbcType=TIMESTAMP}, #{score,jdbcType=INTEGER}
)

mapper.select:
select sign_in.id, ps_user.nick_name,sign_in.sign_in_time,course.course_name from sign_in, course, ps_user where sign_in.signer_id=ps_user.id and sign_in.course_id=course.id AND course.t_id = #{tId, jdbcType=BIGINT}
course.course_name LIKE #{courseName,jdbcType=VARCHAR},
ps_user.sName LIKE #{sName,jdbcType=VARCHAR},
ORDER BY sign_in.sign_in_time DESC limit #{start,jdbcType=TINYINT}, #{count,jdbcType=TINYINT}

select count(sign_in.id) from sign_in, course, ps_user where sign_in.signer_id=ps_user.id and sign_in.course_id=course.id AND course.t_id = #{tId, jdbcType=BIGINT}
course.course_name LIKE #{courseName,jdbcType=VARCHAR},
ps_user.sName LIKE #{sName,jdbcType=VARCHAR},

select sign_in.id, ps_user.nick_name,sign_in.sign_in_time,course.course_name,sign_in.score, sign_in.score_time from sign_in, course, ps_user where sign_in.signer_id=ps_user.id and sign_in.course_id=course.id AND sign_in.signer_id = #{signerId, jdbcType=VARCHAR}
course.course_name LIKE #{courseName,jdbcType=VARCHAR},
ps_user.sName LIKE #{sName,jdbcType=VARCHAR},
ORDER BY sign_in.sign_in_time DESC limit #{start,jdbcType=TINYINT}, #{count,jdbcType=TINYINT}

select count(sign_in.id) from sign_in, course, ps_user where signer_id=ps_user.id and sign_in.course_id=course.id AND sign_in.signer_id = #{signerId, jdbcType=VARCHAR}
course.course_name LIKE #{courseName,jdbcType=VARCHAR},
ps_user.sName LIKE #{sName,jdbcType=VARCHAR},

select

from sign_in
where signer_id = #{signerId,jdbcType=BIGINT}
AND course_id = #{courseId,jdbcType=BIGINT}
AND sign_in_time > #{startTime,jdbcType=TIMESTAMP}
AND sign_in_time < #{endTime,jdbcType=TIMESTAMP}
